
public class StringEquals {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String str1 = new String("This is");
		String str2 = new String("String");
		
		if(str1.equals(str2)) {
			System.out.println("같다");
		}else {
			System.out.println("다르다");
		}
	}

}
